import React, { Component } from "react";
import { Card } from "components/Card/Card.jsx";
import { FormInputs } from "components/FormInputs/FormInputs.jsx";
import { UserCard } from "components/UserCard/UserCard.jsx";
import Button from "components/CustomButton/CustomButton.jsx";

import avatar from "assets/img/faces/face-3.jpg";
import {
  Grid,
  Row,
  Col,
  FormGroup,
  ControlLabel,
  FormControl
} from "react-bootstrap";



class Location_form extends Component {
    state = {
        form: {location_name: "",
               location_type: "",
               location_lat: "", 
               location_lng: "",
               isEdit : false
            },
        btnName: "저장",
        btnClass: "ui primary button submit-button"
    };

    isEmpty(obj) {
        return Object.entries(obj).length === 0 && obj.constructor === Object;
    };

    componentDidUpdate(prevProps) {
        if(prevProps !== this.props && !this.isEmpty(this.props.location_data)) {
            this.setState({
                form: { ...this.props.location_data, isEdit: true },
                btnName: "Update",
                btnClass: "ui orange button submit-button"

            })
            console.log("update");
        }
    };

    onFormSubmit = event => {
        // prevent form submit
        event.preventDefault();

        if(this.formValidation() ) {
            // send form data to location
            this.props.onFormSubmit(this.state.form);
        };

        // change the button to save 
        this.setState({
            btnName: "Save",
            btnClass: "ui primary button submit-button"
        });

        // clear form Fields
        this.clearFormFields();
    };

    //폼태그 유효성 검사 함수
    formValidation = () => {
        // location name
        if(document.getElementsByName("location_name")[0].value === "") {
            alert("Enter location name");
            return false;
        }

        // location explain
        if(document.getElementsByName("location_type")[0].value === "") {
            alert("Enter location explain");
            return false;
        }

        // location lat
        if(document.getElementsByName("location_lat")[0].value === "") {
            alert("Enter location lat");
            return false;
        }

        // location lng
        if(document.getElementsByName("location_lng")[0].value === "") {
            alert("Enter location lng");
            return false;
        }
        return true;
    };

    clearFormFields = () => {
        // change form state
        this.setState({
            form: {
                location_name: "",
                location_type: "",
                location_lat: "", 
                location_lng: "",
                isEdit : false
            }}
        );
        //clear form fields
        document.querySelector(".map_form").reset();
    };

    handleChange = event => {
        if(this.state.form.isEdit == true) {
            const { name, value } = event.target;
            let form = this.state.form;
            form[name] = value;
            this.setState({ form });
        } else {
            let form = {
                location_name: document.getElementsByName("location_name")[0].value,
                location_type: document.getElementsByName("location_type")[0].value,
                location_lat: document.getElementsByName("location_lat")[0].value, 
                location_lng: document.getElementsByName("location_lng")[0].value,
            }
            this.setState({ form });
        }

    };

    render() {
        return (
            <div className="content">
            <Grid fluid>
            <Row>
                <Col md={2} />
                <Col md={8}>
                <Card
                    title="data input"
                    content={
                    <form onSubmit={this.onSubmit}>

                        <FormInputs
                        ncols={["col-md-5", "col-md-7"]}
                        properties={[
                            {
                            label: "Username",
                            type: "text",
                            bsClass: "form-control",
                            placeholder: "Username",
                            defaultValue: this.state.name,
                            onChange: this.onChange
                            },
                            {
                            label: "Email address",
                            type: "email",
                            bsClass: "form-control",
                            placeholder: "email",
                            defaultValue: this.state.email,
                            onChange: this.onChange
                            
                            }
                        ]}
                        />
                        <FormInputs
                                ncols={["col-md-6", "col-md-6"]}
                                properties={[
                                    {
                                    label: "Password",
                                    type: "password",
                                    name: "password",
                                    bsClass: "form-control",
                                    placeholder: "Password",
                                    value: this.state.password,
                                    onChange: this.onChange
                                    },
                                    {
                                    label: "Password confirm",
                                    type: "password",
                                    name: "pwconfirm",
                                    bsClass: "form-control",
                                    placeholder: "Password confirm",
                                    value: this.state.pwconfirm,
                                    onChange: this.onChange
                                    }
                                ]}
                                />

                        <FormInputs
                        ncols={["col-md-12"]}
                        properties={[
                            {
                            label: "address",
                            type: "text",
                            bsClass: "form-control",
                            placeholder: "Home address",
                            defaultValue: this.state.address,
                            onChange: this.onChange
                            }
                        ]}
                        />
                        <FormInputs
                        ncols={["col-md-6", "col-md-6"]}
                        properties={[
                            {
                            label: "City",
                            type: "text",
                            bsClass: "form-control",
                            placeholder: "City",
                            defaultValue: this.state.city,
                            onChange: this.onChange
                            },
                            {
                            label: "Country",
                            type: "text",
                            bsClass: "form-control",
                            placeholder: "Country",
                            defaultValue: this.state.country,
                            onChange: this.onChange
                            },
                        ]}
                        />
                        <FormInputs
                        ncols={["col-md-8"]}
                        properties={[
                        {
                            label: "Birthday",
                            type: "date",
                            bsClass: "form-control",
                            placeholder: "Country",
                            defaultValue: this.state.birthday,
                            onChange: this.onChange
                        },
                        ]}
                    />
                        <Row>
                        <Col md={12}>
                            <FormGroup controlId="formControlsTextarea">
                            <ControlLabel>About Me</ControlLabel>
                            <FormControl
                                rows="5"
                                componentClass="textarea"
                                bsClass="form-control"
                                placeholder="Here can be your description"
                                value={this.state.about}
                                onChange={this.onChange}
                            />
                            </FormGroup>
                        </Col>
                        </Row>
                        <Button bsStyle="info" pullRight fill type="submit">
                        Update Profile
                        </Button>
                        <div className="clearfix" />
                    </form>
                    }
                />
                </Col>
                <Col md={4}>
                <UserCard
                    bgImage="https://ununsplash.imgix.net/photo-1431578500526-4d9613015464?fit=crop&fm=jpg&h=300&q=75&w=400"
                    avatar={avatar}
                    name="Mike Andrew"
                    userName="michael24"
                    description={
                    <span>
                        "Lamborghini Mercy
                        <br />
                        Your chick she so thirsty
                        <br />
                        I'm in that two seat Lambo"
                    </span>
                    }
                    socials={
                    <div>
                        <Button simple>
                        <i className="fa fa-facebook-square" />
                        </Button>
                        <Button simple>
                        <i className="fa fa-twitter" />
                        </Button>
                        <Button simple>
                        <i className="fa fa-google-plus-square" />
                        </Button>
                    </div>
                    }
                />
                </Col>
            </Row>
            </Grid>
            </div>
        );
    }
}

export default Location_form;
